package com.hughes.exercises;

/*
 display Alphabets reverse order using do while.
 */



/**
 * @author bikas
 *
 */

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='z';
		do {
			System.out.print(ch+" ");
			ch-=1;
			
		}while(ch>='a');

	}

}
